import Foundation

final class MockUpFeedRepository: FeedRepository {

    let mockUpFeedData = MockUpFeedData()

    func fetchRecentHomeFeedList() async throws -> HomeFeedList {
        let mockUpNormalFeedList = createMockUpNormalFeedList(imageFeedCount: 950, noImageFeedCount: 50)
        return HomeFeedList(recommendFeed: [], normalFeed: mockUpNormalFeedList)
    }

    private func createMockUpNormalFeedList(imageFeedCount: Int, noImageFeedCount: Int) -> [Feed] {
        var result: [Feed] = []
        for _ in 1...imageFeedCount {
            result.append(mockUpFeedData.createMockUpFeed())
        }
        for _ in 1...noImageFeedCount {
            result.append(mockUpFeedData.createMockUpFeedWithoutThumbnailImage())
        }
        return result.shuffled()
    }
}

extension MockUpFeedRepository {
    func fetchMoreNormalFeed() async throws -> [Feed] {
        return []
    }

    func fetchRecentScrapFeed(userUUID: String) async throws -> [Feed] {
        throw MockUpFeedRepositoryError.noImplementation
    }

    func fetchMoreScrapFeed(userUUID: String) async throws -> [Feed] {
        throw MockUpFeedRepositoryError.noImplementation
    }

    func scrapFeed(_ feed: Feed, userUUID: String) async throws -> Feed {
        throw MockUpFeedRepositoryError.noImplementation
    }

    func unScrapFeed(_ feed: Feed, userUUID: String) async throws -> Feed {
        throw MockUpFeedRepositoryError.noImplementation
    }
}

struct MockUpFeedData {

    func createMockUpFeed() -> Feed {
        return Feed(
            feedUUID: UUID().uuidString,
            writer: createMockUpFeedWriter(),
            title: "이미지가 있는 피드입니다. 이미지가 있는 피드입니다. 이미지가 있는 피드입니다. 이미지가 있는 피드입니다.",
            pubDate: Date(),
            url: "",
            thumbnailURL: "https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2Fodt1V%2FbtrUt8DP6bH%2FBIyRcfXepkYEkKPYcUwv8k%2Fimg.png",
            content: content(),
            scrapCount: Int.random(in: 0...50),
            isScraped: false
        )
    }

    func createMockUpFeedWithoutThumbnailImage() -> Feed {
        return Feed(
            feedUUID: UUID().uuidString,
            writer: createMockUpFeedWriter(),
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.     ",
            pubDate: Date(),
            url: "",
            thumbnailURL: "",
            content: content(),
            scrapCount: Int.random(in: 0...50),
            isScraped: false
        )
    }

    private func createMockUpFeedWriter() -> FeedWriter {
        return FeedWriter(
            userUUID: UUID().uuidString,
            nickname: "Test",
            camperID: "T001",
            ordinalNumber: 7,
            domain: .iOS,
            profileImageURL: "https://firebasestorage.googleapis.com/v0/b/eoljuga-9b868.appspot.com/o/test%2Fburstcamp_logo.png?alt=media&token=27803b7f-fa36-4bc7-874e-b3b071c6cfa9",
            blogTitle: "테스트 블로그"
        )
    }

    private func content() -> String {
        return #"<div id="readability-page-1" class="page"><div><figure data-ke-mobilestyle="widthOrigin" data-filename="boostIcon.png" data-origin-width="250" data-origin-height="250"><span data-url="https://blog.kakaocdn.net/dn/odt1V/btrUt8DP6bH/BIyRcfXepkYEkKPYcUwv8k/img.png" data-lightbox="lightbox"><img src="https://blog.kakaocdn.net/dn/odt1V/btrUt8DP6bH/BIyRcfXepkYEkKPYcUwv8k/img.png" srcset="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&amp;fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2Fodt1V%2FbtrUt8DP6bH%2FBIyRcfXepkYEkKPYcUwv8k%2Fimg.png" onerror="this.onerror=null; this.src='//t1.daumcdn.net/tistory_admin/static/images/no-image-v1.png'; this.srcset='//t1.daumcdn.net/tistory_admin/static/images/no-image-v1.png';" data-filename="boostIcon.png" data-origin-width="250" data-origin-height="250"></span></figure><p data-ke-size="size16">부스트 캠프가 끝이 났다.</p><p data-ke-size="size16">한창 무더웠을 때 시작했는데 이제는 그 때의 더위가 생각나지 않을 정도로 추워졌다.</p><p data-ke-size="size16">부캠하면서 있었던 일을 잊기 전에 적어본다.</p><h2 data-ke-size="size26"><b>부스트캠프 지원</b></h2><p data-ke-size="size16">사실 지원하기 전까지 큰 관심이 없었다. 부스트 캠프라는 것이 있는지도 몰랐다.&nbsp;</p><p data-ke-size="size16">네이버 배너 광고에서 우연히 봤던 것 같다.</p><p data-ke-size="size16">처음에 광고를 따라 들어왔을 때까지도 큰 관심이 없었다.</p><p data-ke-size="size16">"요즘 부트캠프 같은 거 많고 큰 차별점도 없다는데 굳이 해야되나? 코테도 공부 안해서 잘 못 하는데 그 시간에 코테 공부하고 개인 프로젝트 해야지" 라는 생각이었다.</p><p data-ke-size="size16">(지금 생각해보면 진짜 우물 안 개구리...)</p><p data-ke-size="size16">그러다가 지원 마감 이틀 전인가 한 번 더 광고가 떳던 거 같다.</p><p data-ke-size="size16">이 쯤 되면 인연이라했던가.</p><p data-ke-size="size16">그냥 코테 한 번 본다는 느낌으로 지원했다.</p><h2 data-ke-size="size26"><b>챌린지 합격</b></h2><p data-ke-size="size16">챌린지 합격후기는 아래에서 볼 수 있다.</p><figure id="og_1671861442428" contenteditable="false" data-ke-type="opengraph" data-ke-align="alignCenter" data-og-type="article" data-og-title="[부스트캠프 7기 iOS] - 챌린지 합격 후기 (Swift)" data-og-description="조금 늦었지만 부스트캠프 7기 챌린지 합격 후기를 적어본다. 부스트캠프는 네이버 커넥트 재단에서 진행하는 교육 프로그램으로 챌린지/멤버쉽이 나눠져 있다. 나는 모바일 iOS(Swift)에 지원을 " data-og-h